class Action:    """    An object that contains a player action generated by an AI or an interface.    """    def __init__(self, num):        self.index = num        pass    def __str__(self):        if self.index == 0:            return "check"        if self.index == 1:            return "call"        if self.index == 2:            return "bet"        if self.index == 3:            return "raise"        if self.index == 4:            return "fold"        return ""    __repr__ = __str__    # Override the default Equals behavior    def __eq__(self, other):        if isinstance(other, self.__class__):            return self.index == other.index        return False# Action Set : {check-0, call-1, bet-2, raise-3, fold-4}Action_Set_1 = [Action(1), Action(3), Action(4)]Action_Set_2 = [Action(0), Action(2), Action(4)]Action_Set_3 = [Action(1), Action(4)]Action_Empty = []Action_dic = {"call": 0, "fold": 1, "raise": 2}Action_predic = {"call": 0, "fold": 1}